<?php$route = '/taxonomy/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$id = trim(mysql_real_escape_string($id));	$ReturnObject = array();		$Query = "SELECT * FROM taxonomy WHERE Parent_ID = " . $id . " ORDER BY id ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$title = $Database['title'];		$parent_id = $Database['parent_id'];		$level = $Database['level'];				$D = array();		$D['id'] = $id;		$D['title'] = $title;		$D['parent_id'] = $parent_id;		$D['level'] = $level;				array_push($ReturnObject,$D);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>